// export default Chart;


import React from "react";
import PropTypes from "prop-types";

import { format } from "d3-format";

import { ChartCanvas, Chart } from "react-stockcharts";
import {
	BarSeries,
	CandlestickSeries,
} from "react-stockcharts/lib/series";
import { XAxis, YAxis } from "react-stockcharts/lib/axes";

import { discontinuousTimeScaleProvider } from "react-stockcharts/lib/scale";
import { fitWidth } from "react-stockcharts/lib/helper";
import { last } from "react-stockcharts/lib/utils";


export interface ICandleStickStockChartProps {
	chartData: IChart;
	width: number;
	ratio: number;
	type: "svg" | "hybrid"
};

export const CandleStickStockChart = fitWidth(({ chartData, type, width, ratio }: ICandleStickStockChartProps) => {
		const xScaleProvider = discontinuousTimeScaleProvider.inputDateAccessor((d: any) => d.date);
		const {
      data,
			xScale,
			xAccessor,
			displayXAccessor,
		} = xScaleProvider(chartData.dataPoints.reduce((arr, point) => {
      arr.push({
        date: new Date(point.timestamp),
        open: point.open.toFixed(2),
        high: point.high.toFixed(2),
        low: point.low.toFixed(2),
        close: point.close.toFixed(2),
        volume: point.volume.toFixed(2),
      });
      return arr;
    }, [] as any[]));

		const start = xAccessor(last(data));
		const end = xAccessor(data[Math.max(0, data.length - 100)]);
		// const start = xAccessor(last(data[Math.max(end, data.length - 1)]));
		const xExtents = [start, end];

		return (
			<ChartCanvas
        height={400}
				ratio={ratio}
				width={width}
				margin={{ left: 50, right: 50, top: 10, bottom: 30 }}
				type={type}
				seriesName="MSFT"
				data={data}
				xScale={xScale}
				xAccessor={xAccessor}
				displayXAccessor={displayXAccessor}
				xExtents={xExtents}
			>

				<Chart id={1} yExtents={(d: IDataPoint) => [d.high, d.low]}>
					<XAxis axisAt="bottom" orient="bottom"/>
					<YAxis axisAt="right" orient="right" ticks={5} />
					<CandlestickSeries />
				</Chart>
				<Chart id={2} origin={(w: number, h: number) => [0, h - 150]} height={150} yExtents={(d: IDataPoint) => d.volume}>
					<YAxis axisAt="left" orient="left" ticks={5} tickFormat={format(".2s")}/>
					<BarSeries yAccessor={(d: IDataPoint) => d.volume} fill={(d: IDataPoint) => d.close > d.open ? "#6BA583" : "red"} />
				</Chart>
			</ChartCanvas>
		);
});

CandleStickStockChart.propTypes = {
	data: PropTypes.array.isRequired,
	width: PropTypes.number.isRequired,
	ratio: PropTypes.number.isRequired,
	type: PropTypes.oneOf(["svg", "hybrid"]).isRequired,
};

CandleStickStockChart.defaultProps = {
	type: "svg",
};
// CandleStickStockChart = fitWidth(CandleStickStockChart);

export default CandleStickStockChart;
