export function CandlestickChart({dataPoints}: IChart) {
  const divRef = useRef<LegacyRef<HTMLDivElement>>() as any;
  // const [chart, setChart] = useState<CandlestickChartElement>();

  useEffect(() => {
    // new CandlestickChartElement(divRef?.current, dataPoints).draw();
    if (dataPoints && divRef?.current) {
      const chart = (window as any).AmCharts.makeChart( divRef?.current, {
        "type": "serial",
        "theme": "none",
        "dataDateFormat":"YYYY-MM-DD",
        "valueAxes": [ {
          "position": "left"
        } ],
        "graphs": [ {
          "id": "g1",
          "balloonText": "Open:<b>[[open]]</b><br>Low:<b>[[low]]</b><br>High:<b>[[high]]</b><br>Close:<b>[[close]]</b><br>",
          "closeField": "close",
          "fillColors": "#7f8da9",
          "highField": "high",
          "lineColor": "#7f8da9",
          "lineAlpha": 1,
          "lowField": "low",
          "fillAlphas": 0.9,
          "negativeFillColors": "#db4c3c",
          "negativeLineColor": "#db4c3c",
          "openField": "open",
          "title": "Price:",
          "type": "candlestick",
          "valueField": "close"
        } ],
        "chartScrollbar": {
          "graph": "g1",
          "graphType": "line",
          "scrollbarHeight": 30
        },
        "chartCursor": {
          "valueLineEnabled": true,
          "valueLineBalloonEnabled": true
        },
        "categoryField": "date",
        "categoryAxis": {
          "parseDates": true
        },
        "dataProvider": dataPoints,
      
        "export": {
          "enabled": true,
          "position": "bottom-right"
        }
      } );
      
      chart.addListener( "rendered", zoomChart );
      zoomChart();
      
      // this method is called when chart is first inited as we listen for "dataUpdated" event
      function zoomChart() {
        // different zoom methods can be used - zoomToIndexes, zoomToDates, zoomToCategoryValues
        chart.zoomToIndexes( chart.dataProvider.length - 10, chart.dataProvider.length - 1 );
      }  
    }
  }, [dataPoints]);

  return (<div ref={divRef} style={{ width: '100%'}}></div>);
}
export default CandlestickChart;
