import * as React from 'react';
import logo from './logo.svg';
import './App.css';
// import CandlestickChart from './components/CandleStickChart';
import { useEffect, useState } from 'react';
import { CandleStickStockChart } from './components/Chart';

function App() {
  const [chartData, setChartData] = useState<IChart>();

  useEffect(() => {
    (async () => {
      const data = (await (await fetch('http://localhost:3000/api/data/historical?symbol=tsla&interval=1d&range=1mo')).json());
      setChartData(data.payload);
    })();
  }, []);

  return (
    <div className="App">
      <>
        {/* <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.tsx</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header> */}
        {/* { chartData && <CandlestickChart {...chartData} />} */}
        { chartData && <CandleStickStockChart data={chartData.dataPoints.reduce((arr, point) => {
          arr.push({
            date: new Date(point.timestamp),
            open: point.open.toFixed(2),
            high: point.high.toFixed(2),
            low: point.low.toFixed(2),
            close: point.close.toFixed(2),
            volume: point.volume.toFixed(2),
          });
          return arr;
        }, [] as any[])} /> }
      </>
    </div>
  );
}

export default App;
