import React, { LegacyRef, useEffect, useRef } from 'react';

const { Highcharts } = window as any;

// export class Candlestick {
//   timestamp: number;
//   close: number;
//   high: number;
//   low: number;
//   open: number;

//   constructor({ timestamp, open, close, high, low }: TCandleStick) {
//     this.timestamp = parseInt(timestamp.toString());
//     this.open = parseFloat(open.toString());
//     this.close = parseFloat(close.toString());
//     this.high = parseFloat(high.toString());
//     this.low = parseFloat(low.toString());
//   }
// }

// export class VolumeBar {
//   timestamp: number;
//   volume: number;

//   constructor({ timestamp, volume }: TVolume) {
//     this.timestamp = parseInt(timestamp.toString());
//     this.volume = parseFloat(volume.toString());
//   }
// }

export function CandleStickChart({dataPoints}: IChart) {
  const divRef = useRef<LegacyRef<HTMLDivElement>>() as any;
  // const [chart, setChart] = useState<CandleStickChartElement>();

  useEffect(() => {
    // new CandleStickChartElement(divRef?.current, dataPoints).draw();
    if (dataPoints && divRef?.current) {
      // split the data set into candles and volumes
      const candles = dataPoints.reduce((arr, point) => {
        arr.push([
          point.timestamp,
          point.open.toFixed(2),
          point.high.toFixed(2),
          point.low.toFixed(2),
          point.close.toFixed(2),
        ]);
        return arr;
      }, [] as any[]);
      const volumes = dataPoints.reduce((arr, point) => {
        arr.push([
          point.timestamp,
          point.volume
        ]);
        return arr;
      }, [] as any[]);;


      const chart = Highcharts.stockChart(divRef?.current, {
        yAxis: [{
            labels: {
                align: 'left'
            },
            height: '80%',
            resize: {
                enabled: true
            }
        }, {
            labels: {
                align: 'left'
            },
            top: '80%',
            height: '20%',
            offset: 0
        }],
        tooltip: {
            shape: 'square',
            headerShape: 'callout',
            borderWidth: 0,
            shadow: false,
            positioner: (width: number, height: number, point: any) => {
                // var chart = this.chart,
                let position;

                if (point.isHeader) {
                    position = {
                        x: Math.max(
                            // Left side limit
                            chart.plotLeft,
                            Math.min(
                                point.plotX + chart.plotLeft - width / 2,
                                // Right side limit
                                chart.chartWidth - width - chart.marginRight
                            )
                        ),
                        y: point.plotY
                    };
                } else {
                    position = {
                        x: point.series.chart.plotLeft,
                        y: point.series.yAxis.top - chart.plotTop
                    };
                }

                return position;
            }
        },
        series: [{
            type: 'candlestick',
            id: 'aapl-ohlc',
            name: 'AAPL Stock Price',
            data: candles
        }, {
            type: 'column',
            id: 'aapl-volume',
            name: 'AAPL Volume',
            data: volumes,
            yAxis: 1
        }],
        responsive: {
            rules: [{
                condition: {
                    maxWidth: 800
                },
                chartOptions: {
                    rangeSelector: {
                        inputEnabled: false
                    }
                }
            }]
        }
    });

    }
  }, [dataPoints]);

  return (<div id="chart" ref={divRef} style={{ width: '100%', height: '600px'}}></div>);
}
export default CandleStickChart;
