import 'web-component-essentials';

export class Candlestick {
  timestamp: number;
  close: number;
  high: number;
  low: number;
  open: number;

  constructor({ timestamp, open, close, high, low }: TCandleStick) {
    this.timestamp = parseInt(timestamp.toString());
    this.open = parseFloat(open.toString());
    this.close = parseFloat(close.toString());
    this.high = parseFloat(high.toString());
    this.low = parseFloat(low.toString());
  }
}

export class VolumeBar {
  timestamp: number;
  volume: number;

  constructor({ timestamp, volume }: TVolume) {
    this.timestamp = parseInt(timestamp.toString());
    this.volume = parseFloat(volume.toString());
  }
}

export class CandlestickChartElement extends HTMLCanvasElement {
  private candlesticks: Candlestick[];
  gridColor: string;
  gridTextColor: string;
  mouseHoverBackgroundColor: string;
  mouseHoverTextColor: string;
  greenColor: string;
  redHoverColor: string;
  candleWidth: number;
  marginLeft: number;
  marginRight: number;
  marginTop: number;
  marginBottom: number;
  yStart: number;
  yEnd: number;
  yRange: number;
  yPixelRange: number;
  xEnd: number;
  xGridCells: number;
  drawMouseOverlay: boolean;
  mousePosition: { x: number; y: number; };
  xMouseHover: number;
  yMouseHover: number;
  hoveredCandlestickID: number;
  xPixelRange: number;
  xStart: number;
  xRange: number;
  redColor: string;
  greenHoverColor: string;
  yGridCells: number;

  // Returns an array of attributes that the browser will observe.
  static observedAttributes() {}

  // It’s called when the component is first initialized.
  // It must call super() and can set any defaults or perform other pre-rendering processes.
  constructor() {
    super();

    const context = this.getContext('2d') as CanvasRenderingContext2D;

    context.font = '12px sans-serif';
    context.lineWidth = 1;

    this.attachShadow({ mode: 'open' });

    this.addEventListener('mousemove', this.mouseMoveHandler.bind(this));
    this.addEventListener('mouseout', this.mouseOutHandler.bind(this));

    this.style.backgroundColor = '#252525';

    this.gridColor = '#444444';
    this.gridTextColor = '#aaaaaa';
    this.mouseHoverBackgroundColor = '#eeeeee';
    this.mouseHoverTextColor = '#000000';
    this.greenColor = '#00cc00';
    this.redColor = '#cc0000';
    this.greenHoverColor = '#00ff00';
    this.redHoverColor = '#ff0000';

    this.candleWidth = 5;

    this.marginLeft = 10;
    this.marginRight = 100;
    this.marginTop = 10;
    this.marginBottom = 30;

    this.yStart = 0;
    this.yEnd = 0;
    this.yRange = 0;
    this.yPixelRange = this.height-this.marginTop-this.marginBottom;

    this.xStart = 0;
    this.xEnd = 0;
    this.xRange = 0;
    this.xPixelRange = this.width-this.marginLeft-this.marginRight;

    // these are only approximations, the grid will be divided in a way so the numbers are nice
    this.xGridCells = 16;
    this.yGridCells = 16;

    this.drawMouseOverlay = false;
    this.mousePosition = { x: 0 , y: 0 };
    this.xMouseHover = 0;
    this.yMouseHover = 0;
    this.hoveredCandlestickID = 0;

    this.candlesticks = [];
  }

  // // Called whenever an observed attribute is changed.
  // // Those defined in HTML are passed immediately, but JavaScript can modify them:
  // attributeChangedCallback(propertyName: string, oldValue: any, newValue: any) {
  //   // document.querySelector('hello-world').setAttribute('name', 'Everyone');
  //   // The method may need to trigger a re-render when this occurs.
  // }

  // This function is called when the Web Component is appended to a Document Object Model.
  // It should run any required rendering.
  connectedCallback() {}

  // It’s called when the Web Component is removed from a Document Object Model.
  // This may be useful if you need to clean up, such as removing stored state or
  // aborting Ajax requests.
  disconnectedCallback() {}

  // This function is called when a Web Component is moved from one document to another.
  // You may find a use for this, although I’ve struggled to think of any cases!
  adoptedCallback() {}

  addCandlestick(candlestick: Candlestick) {
    this.candlesticks.push(candlestick);
  }

  mouseMoveHandler(event: MouseEvent) {
    this.mousePosition = this.getMousePos(event);
    this.mousePosition.x += this.candleWidth / 2;
    this.drawMouseOverlay = true;
    if (this.mousePosition.x < this.marginLeft) this.drawMouseOverlay = false;
    if (this.mousePosition.x > this.width - this.marginRight + this.candleWidth)
      this.drawMouseOverlay = false;
    if (this.mousePosition.y > this.height - this.marginBottom)
      this.drawMouseOverlay = false;
    if (this.drawMouseOverlay) {
      this.yMouseHover = this.yToValueCoords(this.mousePosition.y);
      this.xMouseHover = this.xToValueCoords(this.mousePosition.x);
      // snap to candlesticks
      const candlestickDelta = this.candlesticks[1].timestamp - this.candlesticks[0].timestamp;
      this.hoveredCandlestickID = Math.floor( (this.xMouseHover - this.candlesticks[0].timestamp) / candlestickDelta);
      this.xMouseHover = Math.floor(this.xMouseHover / candlestickDelta) * candlestickDelta;
      this.mousePosition.x = this.xToPixelCoords(this.xMouseHover);
      this.draw();
    } else this.draw();
  }

  mouseOutHandler(event: MouseEvent) {
    this.drawMouseOverlay = false;
    this.draw();
  }

  getMousePos(event: MouseEvent) {
    const rect = this.getBoundingClientRect();
    return { x: event.clientX - rect.left, y: event.clientY - rect.top };
  }

  draw() {
    const context = this.getContext('2d') as CanvasRenderingContext2D;

    // clear background
    context.clearRect(0, 0, this.width, this.height);
    this.calculateYRange();
    this.calculateXRange();

    this.drawGrid();

    this.candleWidth = this.xPixelRange / this.candlesticks.length;
    this.candleWidth--;
    if (this.candleWidth % 2 === 0) this.candleWidth--;

    for (let color, candlestick, i = 0; i < this.candlesticks.length; ++i) {
      candlestick = this.candlesticks[i];
      color = candlestick.close > candlestick.open ? this.greenColor : this.redColor;

      if (i === this.hoveredCandlestickID) {
        if (color === this.greenColor) color = this.greenHoverColor;
        else if (color === this.redColor) color = this.redHoverColor;
      }

      // draw the wick
      this.drawLine(
        this.xToPixelCoords(candlestick.timestamp),
        this.yToPixelCoords(candlestick.low),
        this.xToPixelCoords(candlestick.timestamp),
        this.yToPixelCoords(candlestick.high),
        color
      );

      // draw the candle
      this.fillRect(
        this.xToPixelCoords(candlestick.timestamp) - Math.floor(this.candleWidth / 2),
        this.yToPixelCoords(candlestick.open),
        this.candleWidth,
        this.yToPixelCoords(candlestick.close) - this.yToPixelCoords(candlestick.open),
        color
      );
    }

    // draw mouse hover
    if (this.drawMouseOverlay && context) {
      // price line
      context.setLineDash([5, 5]);
      this.drawLine(0, this.mousePosition.y, this.width, this.mousePosition.y, this.mouseHoverBackgroundColor);
      context.setLineDash([]);
      let str = this.roundPriceValue(this.yMouseHover).toString();
      let textWidth = context.measureText(str).width;
      this.fillRect(this.width - 70, this.mousePosition.y - 10, 70, 20, this.mouseHoverBackgroundColor);
      context.fillStyle = this.mouseHoverTextColor;
      context.fillText(str, this.width - textWidth - 5, this.mousePosition.y + 5);

      // time line
      context.setLineDash([5, 5]);
      this.drawLine(this.mousePosition.x, 0, this.mousePosition.x, this.height, this.mouseHoverBackgroundColor);
      context.setLineDash([]);
      str = this.formatDate(new Date(this.xMouseHover)).toString();
      textWidth = context.measureText(str).width;
      this.fillRect(this.mousePosition.x - textWidth / 2 - 5, this.height - 20, textWidth + 10, 20, this.mouseHoverBackgroundColor);
      context.fillStyle = this.mouseHoverTextColor;
      context.fillText(str, this.mousePosition.x - textWidth / 2, this.height - 5);

      // data
      let yPos = this.mousePosition.y - 95;
      if (yPos < 0) {
        yPos = this.mousePosition.y + 15;
      }

      this.fillRect(this.mousePosition.x + 15, yPos, 100, 80, this.mouseHoverBackgroundColor);
      const color =
        this.candlesticks[this.hoveredCandlestickID].close >
        this.candlesticks[this.hoveredCandlestickID].open ? this.greenColor : this.redColor;
      this.fillRect(this.mousePosition.x + 15, yPos, 10, 80, color);
      context.lineWidth = 2;
      this.drawRect(this.mousePosition.x + 15, yPos, 100, 80, color);
      context.lineWidth = 1;

      context.fillStyle = this.mouseHoverTextColor;
      context.fillText(
        'O: ' + this.candlesticks[this.hoveredCandlestickID].open,
        this.mousePosition.x + 30,
        yPos + 15
      );
      context.fillText(
        'C: ' + this.candlesticks[this.hoveredCandlestickID].close,
        this.mousePosition.x + 30,
        yPos + 35
      );
      context.fillText(
        'H: ' + this.candlesticks[this.hoveredCandlestickID].high,
        this.mousePosition.x + 30,
        yPos + 55
      );
      context.fillText(
        'L: ' + this.candlesticks[this.hoveredCandlestickID].low,
        this.mousePosition.x + 30,
        yPos + 75
      );
    }
  }

  drawGrid() {
    const context = this.getContext('2d') as CanvasRenderingContext2D ;

    // roughly divide the yRange into cells
    const yGridSize = this.yRange / this.yGridCells;
  
    // try to find a nice number to round to
    let niceNumber = Math.pow(10, Math.ceil(Math.log10(yGridSize)));
    if (yGridSize < 0.25 * niceNumber) niceNumber = 0.25 * niceNumber;
    else if (yGridSize < 0.5 * niceNumber) niceNumber = 0.5 * niceNumber;
  
    // find next largest nice number above yStart
    const yStartRoundNumber = Math.ceil(this.yStart / niceNumber) * niceNumber;
    // find next lowest nice number below yEnd
    const yEndRoundNumber = Math.floor(this.yEnd / niceNumber) * niceNumber;
  
    for (let textWidth, y = yStartRoundNumber; y <= yEndRoundNumber; y += niceNumber) {
      this.drawLine(0, this.yToPixelCoords(y), this.width, this.yToPixelCoords(y), this.gridColor);
      textWidth = context.measureText(this.roundPriceValue(y).toString()).width;
      context.fillStyle = this.gridTextColor;
      context.fillText(this.roundPriceValue(y).toString(), this.width - textWidth - 5, this.yToPixelCoords(y) - 5);
    }
  
    // roughly divide the xRange into cells
    const xGridSize = this.xRange / this.xGridCells;
  
    // try to find a nice number to round to
    niceNumber = Math.pow(10, Math.ceil(Math.log10(xGridSize)));
    if (xGridSize < 0.25 * niceNumber) {
      niceNumber = 0.25 * niceNumber;
    } else if (xGridSize < 0.5 * niceNumber) {
      niceNumber = 0.5 * niceNumber;
    }

    // find next largest nice number above yStart
    const xStartRoundNumber = Math.ceil(this.xStart / niceNumber) * niceNumber;
    // find next lowest nice number below yEnd
    const xEndRoundNumber = Math.floor(this.xEnd / niceNumber) * niceNumber;
  
    // if the total x range is more than 5 days, format the timestamp as date instead of hours
    let formatAsDate = (this.xRange > 60 * 60 * 24 * 1000 * 5);
  
    for (let day, month, minutes, date, dateStr, x = xStartRoundNumber; x <= xEndRoundNumber; x += niceNumber) {
      this.drawLine(this.xToPixelCoords(x), 0, this.xToPixelCoords(x), this.height, this.gridColor);
      date = new Date(x);
      dateStr = '';
      if (formatAsDate) {
        day = date.getDate();
        // if (day < 10) day = '0' + day;
        month = date.getMonth() + 1;
        // if (month < 10) month = '0' + month;
        dateStr = `${(day < 10) ? '0' + day : day}.${(month < 10) ? '0' + month : month}`;
      } else {
        minutes = date.getMinutes();
        if (minutes < 10) {
          minutes = '0' + minutes;
        }
        dateStr = date.getHours() + ':' + minutes;
      }
      context.fillStyle = this.gridTextColor;
      context.fillText(dateStr, this.xToPixelCoords(x) + 5, this.height - 5);
    }
  }

  calculateYRange() {
    for (let i = 0; i < this.candlesticks.length; ++i) {
      if (i === 0) {
        this.yStart = this.candlesticks[i].low;
        this.yEnd = this.candlesticks[i].high;
      } else {
        if (this.candlesticks[i].low < this.yStart) {
          this.yStart = this.candlesticks[i].low;
        }
        if (this.candlesticks[i].high > this.yEnd) {
          this.yEnd = this.candlesticks[i].high;
        }
      }
    }
    this.yRange = this.yEnd - this.yStart;
  }

  calculateXRange() {
    this.xStart = this.candlesticks[0].timestamp;
    this.xEnd = this.candlesticks[this.candlesticks.length - 1].timestamp;
    this.xRange = this.xEnd - this.xStart;
  }

  yToPixelCoords(y: number) {
    return (this.height - this.marginBottom - ((y - this.yStart) * this.yPixelRange) / this.yRange);
  }

  xToPixelCoords(x: number) {
    return this.marginLeft + ((x - this.xStart) * this.xPixelRange) / this.xRange;
  }

  yToValueCoords(y: number) {
    return (this.yStart + ((this.height - this.marginBottom - y) * this.yRange) / this.yPixelRange);
  }

  xToValueCoords(x: number) {
    return this.xStart + ((x - this.marginLeft) * this.xRange) / this.xPixelRange;
  }

  drawLine( xStart: number, yStart: number, xEnd: number, yEnd: number, color: string | CanvasGradient | CanvasPattern) {
    const context = this.getContext('2d') as CanvasRenderingContext2D ;
    context.beginPath();
    // to get a crisp 1 pixel wide line, we need to add 0.5 to the coords
    context.moveTo(xStart + 0.5, yStart + 0.5);
    context.lineTo(xEnd + 0.5, yEnd + 0.5);
    context.strokeStyle = color;
    context.stroke();
  }

  fillRect(x: number, y: number, width: number, height: number, color: string | CanvasGradient | CanvasPattern) {
    const context = this.getContext('2d') as CanvasRenderingContext2D ;
    context.beginPath();
    context.fillStyle = color;
    context.rect(x, y, width, height);
    context.fill();
  }
  
  drawRect(x: number, y: number, width: number, height: number, color: string | CanvasGradient | CanvasPattern) {
    const context = this.getContext('2d') as CanvasRenderingContext2D ;
    context.beginPath();
    context.strokeStyle = color;
    context.rect(x, y, width, height);
    context.stroke();
  }

  formatDate(date: Date) {
    const day = date.getDate();
    const month = date.getMonth() + 1;
    const hours = date.getHours();
    const minutes = date.getMinutes();
    const year = date.getFullYear();

    return `${(day < 10) ? '0' + day : day}/${(month < 10) ? '0' + month : month}/${year} - ${(hours < 10) ? '0' + hours : hours}:${(minutes < 10) ? '0' + minutes : minutes}`;
  }
  
  roundPriceValue(value: number) {
    if (value > 1.0) return Math.round(value * 100) / 100;
    if (value > 0.001) return Math.round(value * 1000) / 1000;
    if (value > 0.00001) return Math.round(value * 100000) / 100000;
    if (value > 0.0000001) return Math.round(value * 10000000) / 10000000;
    else return Math.round(value * 1000000000) / 1000000000;
  }
}
window.customElements.define('candlestick-chart', CandlestickChartElement, { extends: 'canvas' });


